sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var payload = JSON.parse(current.payload);
    if (payload.event.type != 'emoji_changed') return;

    var event = payload.event;

    var text = "";
    switch (event.subtype) {
        case ("add"):
            if (event.value.indexOf("alias:") == 0) {
                text = "A new emoji has been *aliased*! `:" + event.name + ":` pointing to :point_right: `:" + event.value.split(":")[1] + ":`";
            } else {
                text = "A new emoji has been *added*! `:" + event.name + ":` :" + event.name + ":";
                var slackMessageBlocks = [];
                var message = {};
                message.type = "section";
                message.text = {
                    "type": "mrkdwn",
                    "text": text
                };
                slackMessageBlocks.push(message);
                message = {};
                message.type = "section";
                message.text = {
                    "type": "plain_text",
                    "emoji": true,
                    "text": ":" + event.name + ":"
                };
                message.accessory = {
                    "type": "image",
                    "image_url": event.value,
                    "alt_text": ":" + event.name + ":",
                };
                slackMessageBlocks.push(message);
				text = {
					text: text,
					blocks: slackMessageBlocks
				};
            }
            break;
        case ("remove"):
            text = "An emoji has been *removed*! `:" + event.names[0] + ":`";
            break;
        case ("rename"):
            text = "An emoji has been *renamed* from `:" + event.old_name + ":` to `:" + event.new_name + ":`";
            break;
    }


    var message_details = {
        "channel": gs.getProperty('x_snc_slackerbot.SlackerBot.channel.emoji-alerts')
    };
    var sendIt = new x_snc_slackerbot.Slacker().send_chat(message_details, text, false);

})(current, previous);
